mapper.select:

select bno,title,link,image,author,price,discount,publisher,pubdate,isbn,description
from (select rownum rn, bno, title, link,image,author,price,discount,publisher,pubdate,isbn,description
	from book where rownum <= #{pageNum} * #{amount} 
	)
	where rn > (#{pageNum} -1 ) * #{amount}
