mapper.select:

select notice_no, notice_title, notice_content, notice_regdate, notice_updatedate, notice_readcount
from (select rownum rn, notice_no, notice_title, notice_content, notice_regdate, notice_updatedate, notice_readcount
		from notice where



rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum} -1 ) * #{amount}
